sys_script_include.script:
var GetEmailAddress = Class.create();
  //Estende a classe global.AbstractAjaxProcessor
GetEmailAddress.prototype = Object.extendsObject(global.AbstractAjaxProcessor,{
  // Defina a função getEmail.
   // Cria um GlideRecord para a tabela User.
   // Use o sysparm_userID passado do lado do cliente para recuperar um registro da tabela User.
   // Retorna o endereço de e-mail para o registro solicitado
  getEmail: function() {
    var userRecord = new GlideRecord("sys_user");
    userRecord.get(this.getParameter('sysparm_userID'));
    return userRecord.email + '';
  },
  type: 'GetEmailAddress'
});
